import {useState} from 'react';
//stylesheet
import './App.css';

//fonts
import "./fonts/ARCO.ttf";
import "./fonts/ARCOTypography.woff";
import "./fonts/ARCOTypography.woff2";

//components
import Board from './components/Board/Board';
import ScoreBoard from './components/ScoreBoard/ScordeBoard';
import NamesInput from './components/NamesInput/NamesInput';

const winningPositions = [
  [0, 1, 2],
  [3, 4, 5],
  [6, 7, 8],
  [0, 3, 6],
  [1, 4, 7],
  [2, 5, 8],
  [0, 4, 8],
  [2, 4, 6],
];

const App = () => {

  const [turn, setTurn] = useState('X');
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [winningSquares, setWinningSquares] = useState([]);
  const [score, setScore] = useState({
    X:0,
    O:0,
  });
  const [name, setName] = useState({
    p1:'',
    p2:'',
  });

  const reset = () => {
    setTurn('X');
    setSquares(Array(9).fill(null));
    setWinningSquares([]);
  }

  const endGame = (result, winningPositions) => {
    setTurn(null);
    if(result !== null){
      setScore({
        ...score,
        [result]: score[result] + 1,
      })
    }

    setWinningSquares(winningPositions);
    setTimeout(reset, 2000);
  }

  const checkForWinner = newSquares => {
    for(let i = 0; i < winningPositions.length; i++) {
      const [a,b,c] = winningPositions[i];
      if(newSquares[a] && newSquares[a] === newSquares[b] && newSquares[a] === newSquares[c]){
        endGame(newSquares[a], winningPositions[i]);
        return
      }
    }

    if(!newSquares.includes(null)) {
      endGame(null, Array.from(Array(10).keys()));
      return
    }
    setTurn(turn == 'X' ? 'O': 'X');
  }

  const handleClick = square => {
    let newSquares = [...squares];
    newSquares.splice(square, 1, turn);
    setSquares(newSquares);
    checkForWinner(newSquares);
  }

  const startGame = () => {
    setName({
      ...name,
      p1: document.getElementsByName('p1').value,
      p2: document.getElementsByName('p2').value,
    });
    console.log([document.getElementsByName('p1'),document.getElementsByName('p2')]);
    reset();
  }

  return (
    <div className="container">
      <NamesInput onClick={startGame}/>
      <div className='boardContainer'>
        <Board
          winningSquares={winningSquares}
          turn={turn}
          squares={squares} 
          onClick={handleClick}
        />
        <ScoreBoard scoreO={score.O} scoreX={score.X} />
      </div>
    </div>
  );
}

export default App;
